package com.latiff.nutrition.uiimport android.app.Activityimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.view.View.GONEimport android.view.View.VISIBLEimport androidx.databinding.DataBindingUtilimport androidx.navigation.NavControllerimport androidx.navigation.NavDestinationimport androidx.navigation.Navigationimport com.latiff.nutrition.R import com.latiff.nutrition.utiles.Utilesimport dagger.hilt.android.AndroidEntryPointimport kotlinx.android.synthetic.main.activity_main.*import kotlinx.android.synthetic.main.toolbar.*import kotlinx.android.synthetic.main.toolbar.view.*@AndroidEntryPointclass MainActivity : AppCompatActivity() {     override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView( R.layout.activity_main)         var navigation = Navigation.findNavController(            this,            R.id.fragment_container        )        navigation.addOnDestinationChangedListener(navDestinationListener)         backBtn.setOnClickListener {             onBackPressed()         }    }    val navDestinationListener = object : NavController.OnDestinationChangedListener {        override fun onDestinationChanged(            controller: NavController,            destination: NavDestination,            arguments: Bundle?        ) {            toolbar.title.text = destination.label            when (destination.id) {                R.id.homeFragment -> backBtn.visibility = GONE                else -> backBtn.visibility = VISIBLE            }        }    }}